mapper.insert:
insert into tb_user (
  userName, password, name, age, sex, birthday, created, updated
) values (
  #{userName}, #{password}, #{name}, #{age}, #{sex}, #{birthday}, now(), now()
)

mapper.update:
update tb_user
userName = #{userName}, password = #{password}, name = #{name}, age = #{age}, sex = #{sex}, birthday = #{birthday}, updated = now()
where id = #{id}
